import React from 'react';
import './App.css';

const data = [
  {id: "Q", letter: "Q", src: ""},
  {id: "W", letter: "W", src: ""},
  {id: "E", letter: "E", src: ""},
  {id: "A", letter: "A", src: ""},
  {id: "S", letter: "S", src: ""},
  {id: "D", letter: "D", src: ""},
  {id: "Z", letter: "Z", src: ""},
  {id: "X", letter: "X", src: ""},
  {id: "C", letter: "C", src: ""}

]

class Drumpad extends React.Component {

  render() {
       return (
          <div className="drum-pad" />
      )
  }
}

class App extends React.Component {
  constructor(props){
    super(props)
  }

  render() {
    return(
      <div id="drum-machine">
        <div id="display">
          <h1>Display</h1>
          <br/>
          <br/>
          {data.map(d => (
            <Drumpad />
          ))}
          </div>
        {/* <Drumpad /> */}
      </div>
    )
  }
}


export default App

