import React from 'react';
import './App.css';

function App() {
  return (
    // outer container = id: drum-machine
    <div id="drum-machine">
       {/* Within drum-machine id: display */}
      <div id="display">

        {/* Within drum-machine -  9 clickable drum pads, className: drum-pad, unique id that describes the audio clip, AND inner text that corresponds Q, W, E, A, S, D, Z, X, C. The drum pads MUST be in this order. */}
        <div className="drum-pad">Q</div>
        <div className="drum-pad"></div>
        <div className="drum-pad"></div>
        <div className="drum-pad"></div>
        <div className="drum-pad"></div>
        <div className="drum-pad"></div>
        <div className="drum-pad"></div>
        <div className="drum-pad"></div>
        <div className="drum-pad"></div>
      </div>

    </div>
  );
}

export default App;



