import React from 'react';
import './drumpad.css';

const data = [
    {id: "Q", letter: "Q", src: ""},
    {id: "W", letter: "W", src: ""},
    {id: "E", letter: "E", src: ""},
    {id: "A", letter: "A", src: ""},
    {id: "S", letter: "S", src: ""},
    {id: "D", letter: "D", src: ""},
    {id: "Z", letter: "Z", src: ""},
    {id: "X", letter: "X", src: ""},
    {id: "C", letter: "C", src: ""}

]

class Drumpad extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            q: "Q",
            w: 'W',
            e: "E",
            a: "A",
            s: "S",
            d: "D",
            z: "Z",
            x: "X",
            c: "C"
        }
      }

      changeTitle = () => {
          this.setState(
              { q: "High Clap" },
            //   { w: "High Clap" },
            //   { e: "High Clap" },
            //   { a: "High Clap" },
            //   { s: "High Clap" },
            //   { d: "High Clap" },
            //   { z: "High Clap" },
            //   { x: "High Clap" },
            //   { c: "High Clap" },
              );
      };



    render() {

        var audio = new Audio("../assets/sound.mp3")

        return (
            <div id="display">
                <div className="drum-pad" id="q" onClick={this.changeTitle}>
                    <audio src='../assets/acoustic_snare.mp3' className="clip" id='q' />{this.state.q} 
                </div>
                <div className="drum-pad" id="w" onClick={this.changeTitle}>
                    <audio src='' className="clip" id='w' />{this.state.w}
                    </div>
                <div className="drum-pad" id="e" onClick={this.changeTitle}>
                <audio src='' className="clip" id='e' />{this.state.e}
                    </div>
                <div className="drum-pad" id="a" onClick={this.changeTitle}>
                <audio src='' className="clip" id='a' />{this.state.a}
                    </div>
                <div className="drum-pad" id="s" onClick={this.changeTitle}>
                <audio src='' className="clip" id='s' />{this.state.s}
                    </div>
                <div className="drum-pad" id="d" onClick={this.changeTitle}>
                <audio src='' className="clip" id='d' />{this.state.d}
                    </div>
                <div className="drum-pad" id="z" onClick={this.changeTitle}>
                <audio src='' className="clip" id='z' />{this.state.z}
                    </div>
                <div className="drum-pad" id="x" onClick={this.changeTitle}>
                <audio src='' className="clip" id='x' />{this.state.x}
                    </div>
                <div className="drum-pad" id="c" onClick={this.changeTitle}>
                <audio src='' className="clip" id='c' />{this.state.c}
                    </div>
            </div>
        )
    }
}

export default Drumpad
