import React from 'react';
import './App.css';
import Drumpad from './Components/drumpad';

class App extends React.Component {
  constructor(props){
    super(props)
  }

  render() {
    return(
      <div id="drum-machine">
        <div id="display">
          <h1>Display</h1>
          </div>
        <Drumpad />
      </div>
    )
  }
}


export default App

