import React from 'react';
import './App.css';

const data = [
  {id: "Qq", letter: "Q", src: ""},
  {id: "Ww", letter: "W", src: ""},
  {id: "Ee", letter: "E", src: ""},
  {id: "Aa", letter: "A", src: ""},
  {id: "Ss", letter: "S", src: ""},
  {id: "Dd", letter: "D", src: ""},
  {id: "Zz", letter: "Z", src: ""},
  {id: "Xx", letter: "X", src: ""},
  {id: "Cc", letter: "C", src: ""}

]

class Drumpad extends React.Component {

  render() {
       return (
          <div className="drum-pad" id={this.props.id}> 
          <p>{this.props.letter}</p>
          </div>
      )
  }
}

class App extends React.Component {
  constructor(props){
    super(props)
  }

  render() {
    return(
      <div id="drum-machine">
        <div id="display">
        <h1>{this.props.letter}</h1>
          {data.map(d, key => (
            <Drumpad key={this.props.id} />
          ))}
          </div>
        {/* <Drumpad /> */}
      </div>
    )
  }
}


export default App

